import { Wrapper, Status } from "@googlemaps/react-wrapper";
import React, { FC, FunctionComponent, useContext, useEffect, useRef, useState } from 'react';
import { LocationContext } from './LocationsPage';

export const MapDisplay = () => {
    const {locationForMap} = useContext(LocationContext);
    const [zoom, setZoom] = useState(3);

    let locationMessageSuffix = '';
    let lat = 0; 
    let lng = 0;

    if (locationForMap) {
        lat = locationForMap.lat;
        lng = locationForMap.long;
        locationMessageSuffix = `for ${locationForMap.name}`;
    }

    const [center, setCenter] = useState<google.maps.LatLngLiteral>({ lat, lng });

    const onIdle = (m: google.maps.Map) => {
        console.log('onIdle');
        setZoom(m.getZoom()!);
        setCenter(m.getCenter()!.toJSON());
    }

    return (
        <>
            <div className ="outline w-100 pa3 mr2">A Map Display {locationMessageSuffix}</div>
            <Wrapper apiKey={""}>
                <Map
                    center={center}
                    onIdle={onIdle}
                    zoom={zoom}
                    style={{}}
                >
                </Map>
            </Wrapper>
            <div id="map"></div>
        </>
    );
};

interface MapProps extends google.maps.MapOptions {
    style: { [key: string]: string };
    onIdle?: (map: google.maps.Map) => void;
    children?: React.ReactNode,
}

const Map: FC<MapProps> = ({onIdle, style, children, ...options}: MapProps) => {
    const ref = useRef<HTMLDivElement>(null);
    const [map, setMap] = useState<google.maps.Map>();

    useEffect(() => {
        if(ref.current && !map) {
            setMap(new window.google.maps.Map(ref.current, {}))
        }
    }, [ref, map]);

    useDeepCompareEffectForMaps(() => {
        if(map) {
            map.setOptions(options);
        }
    }, [map, options])

    useEffect(() => {
        if(map) {
            ['idle'].forEach((eventName) => {
                google.maps.event.clearListeners(map, eventName);
            });

            if (onIdle) {
                map.addListener('idle', () => onIdle(map));
            }
        }
    }, [map, onIdle]);

    return (
        <>
            <div ref={ref} style={style} />
            {React.Children.map(children, (child) => {
                if (React.isValidElement(child)) {
                    return React.cloneElement(child, { map });
                }
            })}
        </>
    );
};

const Marker: FC<google.maps.MarkerOptions> = (options) => {
    const [marker, setMarker] = useState<google.maps.Marker>();

    useEffect(() => {
        if (!marker) {
            setMarker(new google.maps.Marker());
        }

        return () => {
            if(marker) {
                marker?.setMap(null);
            }
        }
    }, [marker]);

    useEffect(() => {
        if(marker) {
            marker.setOptions(options);
        }
    }, [marker, options]);

    return null;
}
