import { Wrapper } from "@googlemaps/react-wrapper";
import { isLatLngLiteral } from "@googlemaps/typescript-guards";
import { createCustomEqual } from "fast-equals";
import React, { EffectCallback, FC, useContext, useEffect, useRef, useState } from 'react';

import { LocationContext } from './LocationsPage';

export const MapWrapper = () => {
    const {locationForMap} = useContext(LocationContext);
    const [zoom, setZoom] = useState(3);

    let locationMessageSuffix = '';
    let lat = 0; 
    let lng = 0;

    if (locationForMap) {
        lat = locationForMap.lat;
        lng = locationForMap.long;
        locationMessageSuffix = `for ${locationForMap.name}`;
    }

    const [center, setCenter] = useState<google.maps.LatLngLiteral>({ lat, lng });

    const onIdle = (m: google.maps.Map) => {
        console.log('onIdle');
        setZoom(m.getZoom()!);
        setCenter(m.getCenter()!.toJSON());
    }

    return (
        <>
            <div className ="outline w-100 pa3 mr2">A Map Display {locationMessageSuffix}</div>
            <Wrapper apiKey={""}>
                <Map
                    center={center}
                    onIdle={onIdle}
                    zoom={zoom}
                    style={{}}
                >
                </Map>
            </Wrapper>
            <div id="map"></div>
        </>
    );
};



const Marker: FC<google.maps.MarkerOptions> = (options) => {
    const [marker, setMarker] = useState<google.maps.Marker>();

    useEffect(() => {
        if (!marker) {
            setMarker(new google.maps.Marker());
        }

        return () => {
            if(marker) {
                marker?.setMap(null);
            }
        }
    }, [marker]);

    useEffect(() => {
        if(marker) {
            marker.setOptions(options);
        }
    }, [marker, options]);

    return null;
};

const deepCompareEqualsForMaps = createCustomEqual(
    (deepEqual) => (a: any, b: any) => {
        if(
            isLatLngLiteral(a) ||
            a instanceof google.maps.LatLng ||
            isLatLngLiteral(b) ||
            b instanceof google.maps.LatLng
        ) {
            return new google.maps.LatLng(a).equals(new google.maps.LatLng(b));
        }

        return deepEqual(a, b);
    }
)

function useDeepCompareMemoize(value: any) {
    const ref = useRef();

    if(!deepCompareEqualsForMaps(value, ref.current)) {
        ref.current = value;
    }

    return ref.current;
}

function useDeepCompareEffectForMaps(
    callback: EffectCallback,
    dependencies: any[]
) {
    useEffect(callback, dependencies.map(useDeepCompareMemoize));
}
